catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	var user_middle_name = '';
	if(newValue.indexOf('true') > -1){
		var ga = new GlideAjax('TRA_getMiddleName');
		ga.addParam('sysparm_name','getMid');
		ga.addParam('sysparm_sys_id', g_user.userID);
		ga.getXML(returnMidName);
		function returnMidName(serverResponse) {
			user_middle_name = serverResponse.responseXML.documentElement.getAttribute('answer');

			var compare = '';
			//g_form.addInfoMessage(g_user.firstName + ' ' + user_middle_name + ' ' + g_user.lastName);
			if(user_middle_name == null){
				user_middle_name = '';
				compare = g_user.firstName + ' ' + g_user.lastName;
			}
			else{
				compare = g_user.firstName + ' ' + user_middle_name + ' ' + g_user.lastName;
			}
			//g_form.addInfoMessage(g_user.firstName + ' ' + user_middle_name + ' ' + g_user.lastName);
			if(g_form.getValue('u_section_1_signature_signed_em') == compare){
				g_form.addInfoMessage('Valid Signature');
			}
			else{
				g_form.addErrorMessage('Invalid Signature');
				g_form.setValue('u_section_1_checkbox_complete', oldValue);
			}
		}

		/*
		g_form.addInfoMessage("Please review the <a href='https://drive.google.com/open?id=1Fgqm0SNc5wBPEOHgj7dYi_KOGvX1d0UY' target='_blank'>Plus3 IT Tuition and Training Reimbursement Policy</a>");
		confirm("Please review the <a href='https://drive.google.com/open?id=1Fgqm0SNc5wBPEOHgj7dYi_KOGvX1d0UY' target='_blank'>Plus3 IT Tuition and Training Reimbursement Policy</a>");
 		*/
	}

	/*
	try {
		var dialog = new GlideModal('x_237286_training_test', false, 600);
	} catch (ex) {
		//gs.error("There was an error doing something", ex);
		//alert("Error: " + ex);
	}
 	*/
	//var dialog = new GlideModal('x_237286_training_test');
	//var dialog = new GlideModal('x_237286_training_terms_and_conditions_dialog',false, 600);
	//dialog.setTitle('Terms and Conditions'); //Set the dialog title
	//dialog.setSize(600,600); //Set the dialog size
	//dialog.adjustSize();
	//dialog.removeCloseDecoration(); //Remove the dialog close icon
	//dialog.setPreference('cancel_url', 'catalog_home.do?sysparm_view=catalog_default'); //Set optional cancel redirect URL
	//dialog.render(); //Open the dialog
	//Type appropriate comment here, and begin script below

}
