catalog_script_client.script:
function onLoad() {
	var ga = new GlideAjax('TRA_hideVars');
	ga.addParam('sysparm_name','hideVars');
	ga.addParam('sysparm_section_num', "2");
	ga.addParam('sysparm_cat_sys_id', g_form.getUniqueValue());
	ga.getXML(hideSection);
}
// The callback function for returning server-side code
function hideSection(serverResponse) {
	// response from the server which needs to be parsed. There does not appear to be other ways to parse.
	//var answer = serverResponse.responseXML.getElementsByTagName("answer");
	var answer = serverResponse.responseXML.documentElement.getAttribute("answer");
	// the answer received is a JSON object, parsing it to read the data
	answer = JSON.parse(answer);
	// boolean returned from script include in array index 0 -> tells if section 1 or 2 should be hidden
	var section_num = answer[0];
	// 1 = hide section 1, 2 = hide section 2
	var i = 0;
	while (i < (answer.length)) {
		g_form.addInfoMessage(answer[i]);
		i = i + 1;
	}
	if(section_num == 1){
		//debugging info message
		g_form.addInfoMessage('Hide section 1');
		//hide vars
		i = 1;
		while (i < (answer.length)) {
			g_form.setDisplay(answer[i],false);
			i = i + 1;
		}
	}
	else if (section_num == 2){
		//debugging error message
		g_form.addInfoMessage('Hide section 2');
		//hide vars
		i = 1;
		while (i < (answer.length)) {
			g_form.setDisplay(answer[i],false);
			i = i + 1;
		}
	}
	else{
		//error message
		g_form.addInfoMessage('not possible case');
	}
}
