sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var estimatedCourse1 = parseFloat(g_form.getValue('u_section_2_course_1_cost').replace(/,/g,"").substring(4));
	var estimatedCourse2 = parseFloat(g_form.getValue('u_section_2_course_2_cost').replace(/,/g,"").substring(4)); 
	var estimatedCourse3 = parseFloat(g_form.getValue('u_section_2_course_3_cost').replace(/,/g,"").substring(4));

	var estimatedTotalCostFloat = estimatedCourse1 + estimatedCourse2 + estimatedCourse3;
	var estimatedTotalCostString = "$" + estimatedTotalCostFloat.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
	g_form.setValue('u_reimbursement_requested', estimatedTotalCostString);
}
