sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	if (newValue == -1){
		g_form.setDisplay('u_section_1_other',true);
		g_form.setMandatory('u_section_1_other', true);
	}
	else{
		g_form.setMandatory('u_section_1_other', false);
		g_form.setDisplay('u_section_1_other',false);
	}

	if (newValue == 2){
		g_form.setDisplay('u_section_1_course_number',true);
		switch(g_form.getValue('u_section_1_course_number')){
			case "1":
				g_form.setValue('u_section_1_course_2_cost', 0.00);
				g_form.setValue('u_section_1_course_3_cost', 0.00);
				g_form.setValue('u_section_1_course_4_cost', 0.00);
				g_form.setValue('u_section_1_course_5_cost', 0.00);
				g_form.setSectionDisplay('course_2', false);
				g_form.setSectionDisplay('course_3', false);
				g_form.setSectionDisplay('course_4', false);
				g_form.setSectionDisplay('course_5', false);
				//alert(1);
				break;

			case "2":
				g_form.setValue('u_section_1_course_3_cost', 0.00);
				g_form.setValue('u_section_1_course_4_cost', 0.00);
				g_form.setValue('u_section_1_course_5_cost', 0.00);
				g_form.setSectionDisplay('course_2', true);
				g_form.setSectionDisplay('course_3', false);
				g_form.setSectionDisplay('course_4', false);
				g_form.setSectionDisplay('course_5', false);
				//alert(2);
				break;

			case "3":
				g_form.setValue('u_section_1_course_4_cost', 0.00);
				g_form.setValue('u_section_1_course_5_cost', 0.00);
				g_form.setSectionDisplay('course_2', true);
				g_form.setSectionDisplay('course_3', true);
				g_form.setSectionDisplay('course_4', false);
				g_form.setSectionDisplay('course_5', false);
				//alert(3);
				break;

			case "4":
				g_form.setValue('u_section_1_course_5_cost', 0.00);
				g_form.setSectionDisplay('course_2', true);
				g_form.setSectionDisplay('course_3', true);
				g_form.setSectionDisplay('course_4', true);
				g_form.setSectionDisplay('course_5', false);
				//alert(4);
				break;

			case "5":
				g_form.setSectionDisplay('course_2', true);
				g_form.setSectionDisplay('course_3', true);
				g_form.setSectionDisplay('course_4', true);
				g_form.setSectionDisplay('course_5', true);
				//alert(5);
				break;

			default:
				g_form.setValue('u_section_1_course_1_cost', 0.00);
				g_form.setValue('u_section_1_course_2_cost', 0.00);
				g_form.setValue('u_section_1_course_3_cost', 0.00);
				g_form.setValue('u_section_1_course_4_cost', 0.00);
				g_form.setValue('u_section_1_course_5_cost', 0.00);
				break;
		}
	}
	else{
		var course2 = parseFloat(g_form.getValue('u_section_1_course_2_cost').replace(/,/g,"").substring(4));
		var course3 = parseFloat(g_form.getValue('u_section_1_course_3_cost').replace(/,/g,"").substring(4));
		var course4 = parseFloat(g_form.getValue('u_section_1_course_4_cost').replace(/,/g,"").substring(4));
		var course5 = parseFloat(g_form.getValue('u_section_1_course_5_cost').replace(/,/g,"").substring(4));
		var current = parseFloat(g_form.getValue('u_section_1_course_cost').replace(/,/g,"").substring(1));

		var newTotalCourse = current - course2 - course3 - course4 - course5;
		var newEstimatedTotalString = "$" + newTotalCourse.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
		g_form.setValue('u_section_1_course_cost', newEstimatedTotalString);

		g_form.setValue('u_section_1_course_2_cost', 0.00);
		g_form.setValue('u_section_1_course_3_cost', 0.00);
		g_form.setValue('u_section_1_course_4_cost', 0.00);
		g_form.setValue('u_section_1_course_5_cost', 0.00);
		g_form.setDisplay('u_section_1_course_number',false);
		g_form.setSectionDisplay('course_2', false);
		g_form.setSectionDisplay('course_3', false);
		g_form.setSectionDisplay('course_4', false);
		g_form.setSectionDisplay('course_5', false);
	}
}
