sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	g_tabs2Sections.deactivate();

	g_form.setMandatory('u_section_1_course_1_name', false);
	g_form.setMandatory('u_section_1_course_1_hours', false);
	g_form.setMandatory('u_section_1_course_1_cost', false);
	g_form.setMandatory('u_section_1_course_1_start_date', false);
	g_form.setMandatory('u_section_1_course_1_end_date', false);
	g_form.setMandatory('u_section_1_course_1_justification', false);

	g_form.setReadOnly('u_section_1_course_1_name', true);
	g_form.setReadOnly('u_section_1_course_1_hours', true);
	g_form.setReadOnly('u_section_1_course_1_cost', true);
	g_form.setReadOnly('u_section_1_course_1_start_date', true);
	g_form.setReadOnly('u_section_1_course_1_end_date', true);
	g_form.setReadOnly('u_section_1_course_1_justification', true);

	g_form.setReadOnly('u_section_1_school', true);
	g_form.setReadOnly('u_section_1_course_1_car_cost', true);
	g_form.setReadOnly('u_section_1_course_1_flight_cost', true);
	g_form.setReadOnly('u_section_1_course_1_everyday_cost', true);
	g_form.setReadOnly('u_section_1_course_1_hotel_cost', true);

	g_form.setReadOnly('u_section_1_school', true);
	g_form.setReadOnly('u_section_1_course_1_training_type', true);
	g_form.setReadOnly('u_section_1_course_1_other', true);
	g_form.setReadOnly('u_section_1_course_1_payment_method', true);
	g_form.setReadOnly('u_section_1_course_1_financial_aid', true);
	g_form.setReadOnly('u_section_1_course_1_course_number', true);


   for(var i = 1; i <= 5; i++)
	   setSection1CoursesReadOnly(i.toString());

   for(var j = 1; j <= 3; j++)
	   setSection2CoursesReadOnly(j.toString());
}
function setSection2CoursesReadOnly(num)
{
    g_form.setReadOnly('u_section_2_course_' + num + '_name', true);
    g_form.setReadOnly('u_section_2_course_' + num + '_cost', true);
    g_form.setReadOnly('u_section_2_course_' + num + '_date', true);
    g_form.setReadOnly('u_section_2_course_' + num + '_grade', true);
}
function setSection1CoursesReadOnly(num)
{
	g_form.setReadOnly('u_section_1_course_' + num + '_name', true);
	g_form.setReadOnly('u_section_1_course_' + num + '_hours', true);
	g_form.setReadOnly('u_section_1_course_' + num + '_cost', true);
	g_form.setReadOnly('u_section_1_course_' + num + '_start_date', true);
	g_form.setReadOnly('u_section_1_course_' + num + '_end_date', true);
	g_form.setReadOnly('u_section_1_course_' + num + '_justification', true);
}
