sys_script_client.script:
/*
	function - gets each estimated cost field for all 5 courses, adds them, and sets the 
	total estimated course cost
	control    - the DHTML widget whose value changed.
	oldValue   - the value the widget had when the record was loaded.
	newValue   -  the value the widget has after the change.
	isLoading  - identifies whether the change occurs as part of a form load.
	isTemplate - identifies whether the change occurs as part of a template load.
*/

function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	//exits function if function is called as part of a form load or has no newValue
	if (isLoading || newValue === '') {
		return;
	}

	var newCourse1 = parseFloat( g_form.getDecimalValue('u_section_1_course_1_cost') );
	var newCourse2 = parseFloat( g_form.getDecimalValue('u_section_1_course_2_cost') ); 
	var newCourse3 = parseFloat( g_form.getDecimalValue('u_section_1_course_3_cost') );
	var newCourse4 = parseFloat( g_form.getDecimalValue('u_section_1_course_4_cost') );
	var newCourse5 = parseFloat( g_form.getDecimalValue('u_section_1_course_5_cost') );

	var newTotalCourse = newCourse1 + newCourse2 + newCourse3 + newCourse4 + newCourse5;

	//regex below adds commas for every three digits for better readability
	var newEstimatedTotalString = "$" + newTotalCourse.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");	
	g_form.setValue('u_section_1_course_cost', newEstimatedTotalString);
}
