sys_script_include.script:
var TRA_hideVars = Class.create();
TRA_hideVars.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	hideVars :function(){
		// return array that holds item variables
		var obj = [];
		var section = this.getParameter('sysparm_section_num');
		obj.push(section);
		//get catalog item variables
		var gr = new GlideRecord('item_option_new');
		var cat_sys_id = this.getParameter('sysparm_cat_sys_id');
		gr.orderBy('order');
		gr.addQuery('cat_item', cat_sys_id);
		gr.query();
		//push all form variable names in object array
		while(gr.next()){
			obj.push(gr.name.toString());
		}
		var i = 0;
		while(i < obj.length){
			var print = i + ' is ' + obj[i];
			gs.info(print);
			i += 1;
		}
		//encode catalog items and send JSON
		var data = new JSON().encode(obj); //JSON formatted string
		return data;
	}
});
