function resetFin1(){
	var HRTask1Name = "FN1" + current.number.substring(4);
	
	var db = new GlideRecord('x_237286_training_records_needing_approval');
	
	db.get('number', HRTask1Name);
	
	db.state = 1;
	db.u_labor_category_string = current.u_section_1_labor_category_string;
	db.u_contract_code_string = current.u_section_1_contract_code_string;
	db.u_proposed_reimbursement = current.u_section_1_immediate_reimbursement_string;
	db.u_hr_approver_notes = current.u_hr_notes;
	db.u_approval = "noProcess";
	db.u_approver = "";
	db.u_work_notes_comments = "";
	
	db.update();
}

function main(){
	resetFin1();
}

main();

