function writeCourses2LongDes(){
	
	var numCour = current.u_section_1_course_number;
	
	task.description += 
		"\n\nCourses \n\tTitle: " + 
		current.u_section_1_course_1_name + 
		"\n\tStart Date: " + 
		current.u_section_1_course_1_start_date + 
		"\n\tEnd Date: " + current.u_section_1_course_1_end_date + 
		"\n\tHours: TBD" +
		"\n\tEstimated Cost: $" + formatCost( 
		current.u_section_1_course_1_cost) + 
		"\n\tJustification: " + 
		current.u_section_1_course_1_justification;
	
	numCour--;

	if( numCour > 0 ){
		task.description += 
			"\n\n\tTitle: " + 
			current.u_section_1_course_2_name + 
			"\n\tStart Date: " + 
			current.u_section_1_course_2_start_date + 
			"\n\tEnd Date: " + 
			current.u_section_1_course_2_end_date + 
			"\n\tHours: TBD" + 
			"\n\tEstimated Cost: $" + formatCost( 
			current.u_section_1_course_2_cost) + 
			"\n\tJustification: " + 
			current.u_section_1_course_2_justification;
	}
	
	else return;
	
	numCour--;
	
	if( numCour > 0 ){
		task.description += 
			"\n\n\tTitle: " + 
			current.u_section_1_course_3_name + 
			"\n\tStart Date: " + 
			current.u_section_1_course_3_start_date + 
			"\n\tEnd Date: " + 
			current.u_section_1_course_3_end_date + 
			"\n\tHours: TBD" + 
			"\n\tEstimated Cost: $" + formatCost( 
			current.u_section_1_course_3_cost) + 
			"\n\tJustification: " + 
			current.u_section_1_course_3_justification;
	}
	
	else return;
	
	numCour--;
	
	if( numCour > 0 ){
		task.description += 
			"\n\n\tTitle: " + 
			current.u_section_1_course_4_name + 
			"\n\tStart Date: " + 
			current.u_section_1_course_4_start_date + 
			"\n\tEnd Date: " + 
			current.u_section_1_course_4_end_date + 
			"\n\tHours: TBD" + 
			"\n\tEstimated Cost: $" + formatCost( 
			current.u_section_1_course_4_cost) + 
			"\n\tJustification: " + 
			current.u_section_1_course_4_justification;
	}
	
	else return;
	
	numCour--;
	
	if( numCour > 0 ){
		task.description += 
			"\n\n\tTitle: " + 
			current.u_section_1_course_5_name + 
			"\n\tStart Date: " + 
			current.u_section_1_course_5_start_date + 
			"\n\tEnd Date: " + 
			current.u_section_1_course_5_end_date + 
			"\n\tHours: TBD" + 
			"\n\tEstimated Cost: $" + formatCost( 
			current.u_section_1_course_5_cost) + 
			"\n\tJustification: " + 
			current.u_section_1_course_5_justification;
	}
	
	else return;
}

function writeExpenditures2LongDes(){
	task.description += 
		"\n\nExpenditures" + 
		"\n\tEstimated Flight Cost: $" + formatCost( 
		current.u_section_1_flight_cost ) + 
