function getDisplayTrainingType( trainingType ){
	
	switch( trainingType ){
		case "0":  return "Certifications";
		case "1":  return "Conference/Seminar";
		case "2":  return "Education";
		case "3":  return "Training with Certification";
		case "4":  return "Training with Certification";
		case "-1": return current.u_section_1_other;
	}
}

function formatCost( strO ){
	return strO.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

function setTaskNumber(){
	task.number = "APP2" + current.number.substring(4);
}

function setOrigNumber(){
	task.u_original_record_number = current.number;
}

function setSectionNumber(){
	task.u_section_number = "Section II - Secondary Reimbursement";
}

function setTaskState(){
	task.u_hr_state = "Open";
}

function setContractCode(){
	task.u_contract_code = current.u_section_1_contract_code_string;
}

function setLaborCategory(){
	task.u_labor_category = current.u_section_1_labor_category_string;
}

function writeOverview2LongDes( trainingType ){
	task.description = 
		"Overview \n\tName: " + current.sys_created_by + 
		"\n\tCreated: " + current.sys_created_on + 
		"\n\tNumber ID: " + current.number + 
		"\n\n\tSchool: " + current.u_section_1_school + 
		"\n\tTraining Type: " + trainingType;
}

//TODO: REFACTOR LOGIC LATER
function writeCourses2LongDes(){
	
	task.description +=
		"\n\nCourses" +
		"\n\tCourse Title: " + current.u_section_2_course_1_name +
		"\n\tCompletion Date: " + 
		current.u_section_2_course_1_date +
		"\n\tFinal Grade: " + current.u_section_2_course_1_grade +
		"\n\tFinal Cost: " + current.u_section_2_course_1_cost;
	
	if( current.u_section_2_course_2_name != "" ){
		task.description +=
		"\n\n\tCourse Title: " + 
		current.u_section_2_course_2_name +
		"\n\tCompletion Date: " + 
		current.u_section_2_course_2_date +
		"\n\tFinal Grade: " + current.u_section_2_course_2_grade +
		"\n\tFinal Cost: " + current.u_section_2_course_2_cost;
	}
	
	if( current.u_section_2_course_3_name != "" ){
		task.description +=
		"\n\n\tCourse Title: " + 
		current.u_section_2_course_3_name +
		"\n\tCompletion Date: " + 
		current.u_section_2_course_3_date +
		"\n\tFinal Grade: " + current.u_section_2_course_3_grade +
		"\n\tFinal Cost: " + current.u_section_2_course_3_cost;
	}
}

function writeExpenditures2LongDes(){
	task.description += 
		"\n\nReimbursement" + 
		"\n\tEstimated Taxi/Uber Costs: $" + 
		formatCost( current.u_section_1_car_cost ) + 
		"\n\tEstimated Per Diem Cost: $" + 
		formatCost( current.u_section_1_everyday_cost ) + 
		"\n\n\tTotal Reimbursement Requested: $" + 
		formatCost( current.u_reimbursement_requested );
}

function setTaskShortDescription( trainingType ){
	task.short_description = current.sys_created_by + " is attending " + current.u_section_1_school + " for " + trainingType;
}

function setTaskLongDescription( trainingType ){
	
	writeOverview2LongDes( trainingType );
	writeCourses2LongDes();
	writeExpenditures2LongDes();
}

function main(){
	
	setTaskNumber();
	setTaskState();
	setOrigNumber();
	setSectionNumber();
	
	var trainingType = getDisplayTrainingType( current.u_section_1_training_type.toString() );
	
	setTaskShortDescription( trainingType );
	setTaskLongDescription( trainingType );
	
	setContractCode();
	setLaborCategory();
}

main();

