function resetFin1(){
	var HRTask1Name = "FN2" + current.number.substring(4);
	
	var db = new GlideRecord('x_237286_training_records_needing_approval');
	
	db.get('number', HRTask1Name);
	
	db.state = 1;
	db.u_labor_category_string = current.u_section_1_labor_category_string;
	db.u_contract_code_string = current.u_section_1_contract_code_string;
	db.u_proposed_reimbursement = current.u_amount_reimbursed;
	db.u_approval = "noProcess";
	db.u_approver = "";
	db.u_work_notes_comments = "";
	
	db.u_course_1_comments = current.u_course_1_code;
	db.u_course_2_comments = current.u_course_2_code;
	db.u_course_3_comments = current.u_course_3_code;
	
	db.u_course_1_approval = current.u_course_1_approval;
	db.u_course_2_approval = current.u_course_2_approval;
	db.u_course_3_approval = current.u_course_3_approval;
	
	db.update();
}

function main(){
	resetFin1();
}

main();

