function resetHRTask1(){
	
	var HRTask1Name = "HR2" + current.number.substring(4);
	
	var db = new GlideRecord('x_237286_training_records_needing_approval');
	
	db.get('number', HRTask1Name);
	
	db.state = 1;
	db.u_approver = "";
	db.u_financial_notes = current.u_financial_notes;
	
	db.u_course_1_approval = "noProcess";
	db.u_course_2_approval = "noProcess";
	db.u_course_3_approval = "noProcess";
	
	db.u_course_1_comments = "";
	db.u_course_2_comments = "";
	db.u_course_3_comments = "";
	
	db.update();
}

function resetMasterDBState(){
	current.state = 14; //awaiting hr 2
}

function main(){
	resetHRTask1();
	resetMasterDBState();
}

main();

